mapper.select:
SELECT ad.*, u.`name`, u.mobile, c.company_name FROM advertisement ad LEFT JOIN company c ON ad.company_id = c.company_id LEFT JOIN t_user u ON u.user_id = ad.monitor_user_id
ad.`status` = 1 and c.`status`=1
AND ad.advertisement_id=#{advertisementId}

SELECT ad.serial_num, ad.advertisement_id, ad.address, ad.lontitude, ad.latitude, ad.ad_content, ad.ad_status, u.`name`, c.company_name, pf.path, pf.type FROM advertisement ad LEFT JOIN company c ON ad.company_id = c.company_id left JOIN user_company uc on uc.company_id=c.company_id LEFT JOIN t_user u ON u.user_id = ad.monitor_user_id left JOIN pic_file pf on (pf.busness_id=ad.advertisement_id AND pf.type=2 AND pf.`status`=1)
ad.`status` = 1 and c.`status`=1
and (ad.address like concat('%',#{key},'%') or ad.serial_num like concat('%',#{key},'%'))
AND ad.ad_spec=#{adSpec}
AND ad.ad_type=#{adType}
and (ad.user_id=#{userId}
OR uc.user_id=#{userId}
)
and ad.ad_status=#{adStatus}
group by ad.advertisement_id order by serial_num+1 asc,ad.create_time desc
