mapper.select:
SELECT u.user_id, u.mobile, u.portrait_url, u.user_type, u.`name`, u.superior_id, u2.`name` AS superior_name, group_concat(c.company_name) as company_name, group_concat(c.company_id) as company_id FROM t_user u left join user_company uc on u.user_id = uc.user_id LEFT JOIN company c ON uc.company_id = c.company_id LEFT JOIN t_user u2 ON u.superior_id=u2.user_id
AND u.`status` =1 and c.`status` =1
and uc.company_id=#{companyId}
and u.user_type=#{userType}
and u.name like CONCAT('%',#{keyWord},'%')
and u.superior_id =#{supUserId}
group by u.user_id order by u.create_time desc

SELECT count(*) from ( select u.* FROM t_user u left join user_company uc on u.user_id = uc.user_id LEFT JOIN company c ON uc.company_id = c.company_id
AND u.`status` =1 and c.`status` =1
and u.company_id=#{companyId}
and u.user_type=#{userType}
and u.name like CONCAT('%',#{keyWord},'%')
and u.superior_id =#{supUserId}
group by u.user_id ) as aa
